mapper.select:
SELECT a.activity_id,a.activity_name,a.state,a.discount_mode, IF(b.shopNumber IS NULL,0,b.shopNumber) shopNumber, if_credit, credit from cere_platform_activity a LEFT JOIN (SELECT COUNT(sign_id) shopNumber,activity_id from cere_activity_sign where state=1 and sign_type=1 GROUP BY activity_id) b ON a.activity_id=b.activity_id where 1=1
and a.activity_name like concat('%',#{activityName},'%')
and a.state=#{state}
and a.discount_mode=#{discountMode}
GROUP BY a.activity_id ORDER BY a.update_time DESC,a.create_time DESC

SELECT a.sign_id,a.shop_id,b.shop_name,b.shop_code,c.productNumber,a.activity_id, a.state from (SELECT a.* FROM (SELECT sign_id,shop_id,state,activity_id,sign_type from cere_activity_sign ORDER BY create_time DESC) a GROUP BY a.shop_id,a.activity_id) a LEFT JOIN cere_platform_shop b ON a.shop_id=b.shop_id LEFT JOIN (SELECT COUNT(product_id) productNumber,sign_id from cere_sign_product GROUP BY sign_id) c ON a.sign_id=c.sign_id where a.activity_id=#{activityId} and a.sign_type=#{signType}
and b.shop_name like concat('%',#{shopName},'%')
and b.shop_code like concat('%',#{shopCode},'%')
and a.state=#{state}
GROUP BY a.shop_id

SELECT COUNT(*)
from cere_activity_sign
where state in (1, 2)
  and shop_id = #{shopId}
  and activity_id = #{activityId}
  and sign_type = 1

SELECT '营销活动保证金' bondType,b.activity_name,a.sign_code,a.bond_money,a.bond_state, IF(a.bond_state=1,a.payment_time,IF(a.bond_state=2,a.return_time,a.create_time)) time from (SELECT sign_id,sign_code,shop_id,activity_id,bond_money,payment_mode, qr_image,state,bond_state,payment_time,return_time,create_time from cere_activity_sign ORDER BY create_time DESC) a LEFT JOIN cere_platform_activity b ON a.activity_id=b.activity_id LEFT JOIN cere_platform_shop c ON a.shop_id=c.shop_id where a.bond_state in (1,2) and b.if_bond=1
and c.shop_name like concat('%',#{shopName},'%')
and a.bond_state=#{state}
and a.payment_time>=#{startTime} and a.payment_time<=#{endTime}
GROUP BY a.shop_id

SELECT activity_id, activity_start_time, activity_end_time, sign_end_time
FROM cere_platform_activity
where activity_id = #{activityId}

SELECT IF(SUM(a.price) IS NULL, 0, SUM(a.price)) money, COUNT(*) orders, COUNT(a.buyer_user_id) users
FROM cere_shop_order a
         LEFT JOIN cere_activity_sign b ON a.shop_id = b.shop_id
where a.payment_state = 1
  and a.state in (2, 3, 4)
  and b.activity_id = #{activityId}
  and b.state = 1
  and a.create_time >= #{activityStartTime}
  and a.create_time <= #{activityEndTime}
  and b.sign_type = 1

SELECT a.city name, COUNT(a.city) value
from cere_shop_visit a
    LEFT JOIN cere_activity_sign b
ON a.shop_id=b.shop_id
where b.activity_id=#{activityId}
  and b.state=1
  and a.visit_time>=#{activityStartTime}
  and a.visit_time<=#{activityEndTime}
  and b.sign_type=1
GROUP BY a.city

SELECT COUNT(*) value,'新客户' as name
from cere_shop_visit a
    LEFT JOIN cere_buyer_user b
ON a.buyer_user_id=b.buyer_user_id
    LEFT JOIN cere_activity_sign c ON a.shop_id= c.shop_id
where c.activity_id=#{activityId}
  and c.state=1
  and a.visit_time>=#{activityStartTime}
  and a.visit_time<=#{activityEndTime}
  and b.create_time>=#{activityStartTime}
  and b.create_time<=#{activityEndTime}
  and c.sign_type=1

SELECT COUNT(*) value,'老客户' as name
from cere_shop_visit a
    LEFT JOIN cere_buyer_user b
ON a.buyer_user_id=b.buyer_user_id
    LEFT JOIN cere_activity_sign c ON a.shop_id= c.shop_id
where c.activity_id=#{activityId}
  and c.state=1
  and a.visit_time>=#{activityStartTime}
  and a.visit_time<=#{activityEndTime}
  and b.create_time<=#{activityStartTime}
  and c.sign_type=1

SELECT COUNT(*) value,'APP' as name
from cere_shop_visit a
    LEFT JOIN cere_activity_sign c
ON a.shop_id= c.shop_id
where c.activity_id=#{activityId}
  and c.state=1
  and a.visit_time>=#{activityStartTime}
  and a.visit_time<=#{activityEndTime}
  and terminal=1
  and c.sign_type=1

SELECT COUNT(*) value,'小程序' as name
from cere_shop_visit a
    LEFT JOIN cere_activity_sign c
ON a.shop_id= c.shop_id
where c.activity_id=#{activityId}
  and c.state=1
  and a.visit_time>=#{activityStartTime}
  and a.visit_time<=#{activityEndTime}
  and terminal=2
  and c.sign_type=1

SELECT COUNT(*) value,'Android' as name
from cere_shop_visit a
    LEFT JOIN cere_activity_sign c
ON a.shop_id= c.shop_id
where c.activity_id=#{activityId}
  and c.state=1
  and a.visit_time>=#{activityStartTime}
  and a.visit_time<=#{activityEndTime}
  and a.system=1
  and c.sign_type=1

SELECT COUNT(*) value,'IOS' as name
from cere_shop_visit a
    LEFT JOIN cere_activity_sign c
ON a.shop_id= c.shop_id
where c.activity_id=#{activityId}
  and c.state=1
  and a.visit_time>=#{activityStartTime}
  and a.visit_time<=#{activityEndTime}
  and a.system=2
  and c.sign_type=1

SELECT IF(SUM(a.number) IS NULL, 0, SUM(a.number))
from cere_order_product a
         LEFT JOIN cere_shop_order b ON a.order_id = b.order_id
         LEFT JOIN cere_activity_sign c ON b.shop_id = c.shop_id
where b.payment_state = 1
  and b.state in (2, 3, 4)
  and c.activity_id = #{activityId}
  and c.state = 1
  and c.sign_type = 1
  and b.create_time >= #{activityStartTime}
  and b.create_time <= #{activityEndTime}
  and b.shop_id = #{shopId}

SELECT COUNT(*)
from cere_shop_visit a
         LEFT JOIN cere_activity_sign b ON a.shop_id = b.shop_id
where b.activity_id = #{activityId}
  and b.state = 1
  and b.sign_type = 1
  and a.shop_id = #{shopId}
  and a.visit_time >= #{activityStartTime}
  and a.visit_time <= #{activityEndTime}

SELECT COUNT(*)
from cere_shop_order a
         LEFT JOIN cere_activity_sign b ON a.shop_id = b.shop_id
where a.payment_state = 1
  and a.state in (2, 3, 4)
  and b.activity_id = #{activityId}
  and b.state = 1
  and b.sign_type = 1
  and a.shop_id = #{shopId}
  and a.create_time >= #{activityStartTime}
  and a.create_time <= #{activityEndTime}

SELECT COUNT(a.buyer_user_id)
from (SELECT shop_id, buyer_user_id
      FROM cere_shop_order
      where payment_state = 1
        and state in (2, 3, 4)
        and shop_id = #{shopId}
        and create_time >= #{activityStartTime}
        and create_time <= #{activityEndTime}
      GROUP BY buyer_user_id) a
         LEFT JOIN cere_activity_sign b ON a.shop_id = b.shop_id
where b.activity_id = #{activityId}
  and b.state = 1
  and b.sign_type = 1

SELECT IF(SUM(a.price) IS NULL, 0, SUM(a.price))
from cere_shop_order a
         LEFT JOIN cere_activity_sign b ON a.shop_id = b.shop_id
where a.payment_state = 1
  and a.state in (2, 3, 4)
  and b.activity_id = #{activityId}
  and b.state = 1
  and b.sign_type = 1
  and a.shop_id = #{shopId}
  and a.create_time >= #{activityStartTime}
  and a.create_time <= #{activityEndTime}

SELECT activity_id
from cere_platform_activity
where (activity_start_time <= #{activityStartTime} and activity_end_time >= #{activityStartTime}
    OR activity_start_time <= #{activityEndTime} and activity_end_time >= #{activityEndTime})
  and state!= 4 and activity_id!= #{activityId}

SELECT *
FROM cere_activity_sign
where sign_id!= #{signId}
  and shop_id=#{shopId}
  and sign_type=1
  and activity_id=#{activityId}
  and state in (0
    , 1)

SELECT *
FROM cere_activity_sign
where sign_id = #{signId}

SELECT *
FROM cere_pay_log
WHERE order_formid = #{formid}

SELECT sign_id
FROM cere_activity_sign
where shop_id = #{shopId}
  and sign_type = #{signType}
  and activity_id = #{activityId}

SELECT if_bond
FROM cere_platform_activity
WHERE activity_id = #{activityId}

