mapper.select:
SELECT activity_id coupon_id, discount_mode as coupon_type, activity_id, activity_name, activity_start_time, activity_end_time, threshold full_money, coupon_content reduce_money FROM cere_platform_activity where state in (2,3)
and activity_name like concat('%',#{search},'%')
and activity_id in (
#{id}
)
order by create_time desc

SELECT activity_id coupon_id, threshold full_money, coupon_content reduce_money
FROM cere_platform_activity
WHERE activity_id = #{activityId}

SELECT a.shop_group_work_id, shop_id, group_name, remark, start_time, end_time, person, effective_time, if_limit, limit_number, if_enable, enable_time, if_add, `state` FROM cere_shop_group_work a LEFT JOIN (SELECT COUNT(*) total,shop_group_work_id FROM cere_collage_order GROUP BY shop_group_work_id) b ON a.shop_group_work_id=b.shop_group_work_id where state in (0,1)
and shop_group_work_id in (
#{id}
)
ORDER BY b.total DESC

SELECT b.product_id, b.product_name, c.original_price, d.product_image image, c.stock_number, a.price, a.sku_id, c.total, b.shop_id, c.original_price, SUM(f.number) number from cere_shop_group_work_detail a LEFT JOIN cere_shop_product b ON a.product_id = b.product_id LEFT JOIN cere_product_sku c ON a.sku_id = c.sku_id LEFT JOIN (SELECT a.product_id, a.product_image from cere_product_image a, cere_shop_product b where a.product_id = b.product_id GROUP BY a.product_id) d ON b.product_id = d.product_id LEFT JOIN cere_shop_group_work e ON a.shop_group_work_id=e.shop_group_work_id LEFT JOIN cere_order_product f ON a.product_id=f.product_id LEFT JOIN cere_collage_order_detail h ON f.order_id=h.order_id LEFT JOIN cere_collage_order g ON h.collage_id=g.collage_id and g.state=1 where e.state=1
and a.shop_group_work_id in (
#{id}
)
GROUP BY f.product_id ORDER BY SUM(f.number) DESC LIMIT 20
