mapper.select:
SELECT a.buyer_label_id, label_name, label_type, meet_conditions, last_consumption_time, consumption_frequency, consumption_money, consumption_day, consumption_start_time, consumption_end_time, frequency_start, frequency_end, money_start, money_end,IF(b.users IS NULL,0,b.users) users FROM cere_platform_label a LEFT JOIN (SELECT buyer_label_id,COUNT(buyer_user_id) users from cere_buyer_label GROUP BY buyer_label_id) b ON a.buyer_label_id=b.buyer_label_id where a.buyer_label_id in (
#{id}
)

mapper.delete:
DELETE FROM cere_platform_label where buyer_label_id in (

  #{id}

)

mapper.select:
SELECT buyer_user_id FROM cere_shop_order
where create_time>=#{consumptionStartTime} and create_time<=#{consumptionEndTime}
GROUP BY buyer_user_id

SELECT buyer_user_id FROM cere_shop_order
GROUP BY buyer_user_id
HAVING COUNT(buyer_user_id)>=#{frequencyStart}

  and COUNT(buyer_user_id)<=#{frequencyEnd}

SELECT buyer_user_id FROM cere_shop_order
where SUM(price)>=#{moneyStart}

  and SUM(price)<=#{moneyEnd}

GROUP BY buyer_user_id

SELECT buyer_user_id FROM cere_shop_order
where 1=1

  and create_time>=#{consumptionStartTime} and create_time<=#{consumptionEndTime}


  and create_time>=#{consumptionStartTime} and create_time<=#{consumptionEndTime}


  and SUM(price)>=#{moneyStart}


  and SUM(price)<=#{moneyEnd}

GROUP BY buyer_user_id
HAVING 1=1

  and COUNT(buyer_user_id)>=#{frequencyStart}


  and COUNT(buyer_user_id)<=#{frequencyEnd}
