mapper.update:
update cere_buyer_user
set credit = credit + #{credit}
where buyer_user_id = #{buyerUserId}

mapper.select:
SELECT a.buyer_user_id,IF(a.wechat_name IS NULL,a.`name`,a.wechat_name) `name`,d.member_level_name, a.phone,a.credit,IF(b.total IS NULL,0,b.total) total,IF(b.buyers IS NULL,0,b.buyers) buyers, a.create_time, a.if_black, b.time, a.register_ip, a.last_login_ip, a.terminal, a.channel_code from cere_buyer_user a LEFT JOIN (SELECT buyer_user_id,SUM(price) total,COUNT(order_id) buyers,MAX(create_time) time from cere_shop_order where state in (2,3,4) GROUP BY buyer_user_id) b ON a.buyer_user_id=b.buyer_user_id LEFT JOIN cere_buyer_label c ON a.buyer_user_id=c.buyer_user_id LEFT JOIN cere_platform_member_level d ON a.member_level_id=d.member_level_id where 1=1
and (a.wechat_name like concat('%',#{name},'%') or a.`name` like concat('%',#{name},'%'))
and a.phone like concat('%',#{phone},'%')
and c.buyer_label_id=#{labelId}
and b.total>=#{minMoney}
and b.total<=#{maxMoney}
and b.buyers>=#{minBuyers}
and b.buyers<=#{maxBuyers}
GROUP BY a.buyer_user_id
HAVING b.time>=#{startTime} and b.time<=#{endTime}
order by a.create_time desc

SELECT buyer_user_id,IF(wechat_name IS NULL,`name`,wechat_name) `name`,
phone,sex,birthday,create_time,head_image from cere_buyer_user
where buyer_user_id=#{buyerUserId}

SELECT b.label_name from cere_buyer_label a
LEFT JOIN cere_platform_label b ON a.buyer_label_id=b.buyer_label_id
where a.buyer_user_id=#{buyerUserId}

SELECT COUNT(*) from cere_order_product a
LEFT JOIN cere_shop_order b ON a.order_id=b.order_id
where b.buyer_user_id=#{buyerUserId}

SELECT COUNT(*) from cere_order_after a
LEFT JOIN cere_shop_order b ON a.order_id=b.order_id
where b.buyer_user_id=#{buyerUserId}

SELECT COUNT(DISTINCT a.order_id) from cere_order_after a
LEFT JOIN cere_shop_order b ON a.order_id=b.order_id
where b.buyer_user_id=#{buyerUserId}

SELECT a.order_formid,b.shop_name,COUNT(c.product_id) products,a.price,a.state from cere_shop_order a LEFT JOIN cere_platform_shop b ON a.shop_id=b.shop_id LEFT JOIN cere_order_product c ON a.order_id=c.order_id where a.buyer_user_id=#{buyerUserId}
and a.order_formid like concat('%',#{orderFormid},'%')
GROUP BY c.product_id

SELECT a.shop_name,a.comment_id,b.product_name from cere_shop_comment a
LEFT JOIN cere_shop_product b ON a.product_id=b.product_id
where a.buyer_user_id=#{buyerUserId}

SELECT b.* FROM cere_buyer_label a
LEFT JOIN cere_platform_label b ON a.buyer_label_id=b.buyer_label_id
where a.buyer_user_id=#{buyerUserId}
