SELECT a.product_id,a.sku_id,b.product_name,e.product_price,a.weight, a.SKU, IF(c.image IS NULL OR c.image='',d.product_image,c.image) image FROM cere_product_sku a LEFT JOIN cere_shop_product b ON a.product_id=b.product_id LEFT JOIN (SELECT sku_id,image,GROUP_CONCAT(sku_value) `value` from cere_sku_name GROUP BY sku_id) c ON a.sku_id=c.sku_id LEFT JOIN (SELECT a.product_id,a.product_image from cere_product_image a,cere_shop_product b where a.product_id=b.product_id GROUP BY a.product_id) d ON a.product_id=d.product_id LEFT JOIN cere_order_product e ON a.sku_id=e.sku_id and e.order_id=#{orderId} where a.sku_id in (
#{item.skuId}
)
