mapper.select:
SELECT a.product_id,b.shop_id,b.shop_name,b.shop_code from cere_shop_product a
LEFT JOIN cere_platform_shop b ON a.shop_id=b.shop_id
where a.product_id=#{productId}

SELECT a.comment_id,IF(b.wechat_name IS NULL,b.`name`,b.wechat_name) `name`,c.product_price,c.product_name,c.image productImage, a.`comment`,a.add_comment,a.image,a.add_image,a.create_time,b.head_image,g.`value`,a.add_time, IF(e.if_like IS NULL,0,e.if_like) if_like,IF(f.likes IS NULL,0,f.likes) likes,a.star,a.impression, a.des,a.delivery,a.attitude,a.shop_id,a.product_id,a.sku_id from cere_shop_comment a LEFT JOIN cere_buyer_user b ON a.buyer_user_id=b.buyer_user_id LEFT JOIN cere_order_product c ON a.sku_id=c.sku_id LEFT JOIN (SELECT GROUP_CONCAT(sku_value) `value`,sku_id FROM cere_sku_name GROUP BY sku_id) g ON a.sku_id=g.sku_id LEFT JOIN cere_buyer_comment_like e ON a.buyer_user_id=e.buyer_user_id and a.comment_id=e.comment_id LEFT JOIN (SELECT comment_id,COUNT(*) likes from cere_buyer_comment_like where if_like=1 GROUP BY comment_id) f ON a.comment_id=f.comment_id where a.buyer_user_id=#{buyerUserId}
and ((a.image IS NOT NULL and a.image != '') OR (a.add_image IS NOT NULL and a.add_image != ''))
GROUP BY a.comment_id ORDER BY a.update_time DESC,a.create_time DESC

SELECT IF(b.wechat_name IS NULL,b.`name`,b.wechat_name) `name`, c.product_price, c.product_name, c.image productImage, b.head_image, g.`value`, ps.shop_id, ps.shop_name, ps.shop_logo, c.product_id, c.sku_id, a.order_id from cere_shop_order a JOIN cere_platform_shop ps ON ps.shop_id=a.shop_id JOIN cere_order_product c ON c.order_id=a.order_id LEFT JOIN cere_shop_comment sc on sc.order_id = a.order_id and sc.sku_id = c.sku_id LEFT JOIN cere_buyer_user b ON a.buyer_user_id=b.buyer_user_id LEFT JOIN (SELECT GROUP_CONCAT(sku_value) `value`,sku_id FROM cere_sku_name GROUP BY sku_id) g ON g.sku_id=c.sku_id where a.buyer_user_id=#{buyerUserId}
and (a.order_id like concat('%', #{search}, '%') or c.product_name like concat('%', #{search}, '%'))
ORDER BY c.order_product_id DESC
