mapper.select:
select

from cere_product_sku
where sku_id = #{skuId,jdbcType=BIGINT}

SELECT sku_value,value_code from cere_sku_name
where
sku_id=#{skuId}

SELECT stock_number from cere_product_sku
where
sku_id=#{skuId}

SELECT a.product_id, a.sku_id, b.product_name, a.weight, a.stock_number, b.if_oversold, a.SKU, c.`value`, b.if_logistics, a.original_price, a.price, b.if_credit, b.credit_limit, IF(c.image IS NULL OR c.image = '', d.product_image, c.image) image FROM cere_product_sku a LEFT JOIN cere_shop_product b ON a.product_id = b.product_id LEFT JOIN ( SELECT sku_id, image, GROUP_CONCAT(sku_value) `value` from cere_sku_name GROUP BY sku_id ) c ON a.sku_id = c.sku_id LEFT JOIN ( SELECT a.product_id, a.product_image from cere_product_image a, cere_shop_product b where a.product_id = b.product_id GROUP BY a.product_id ) d ON a.product_id = d.product_id where a.sku_id in (
#{item.skuId}
)

SELECT a.product_id,a.sku_id,b.product_name,a.original_price,a.weight,a.stock_number,b.if_oversold, a.SKU,c.`value`,b.if_logistics,e.price, IF(c.image IS NULL OR c.image='',d.product_image,c.image) image FROM cere_product_sku a LEFT JOIN cere_shop_product b ON a.product_id=b.product_id LEFT JOIN (SELECT sku_id,image,GROUP_CONCAT(sku_value) `value` from cere_sku_name GROUP BY sku_id) c ON a.sku_id=c.sku_id LEFT JOIN (SELECT a.product_id,a.product_image from cere_product_image a,cere_shop_product b where a.product_id=b.product_id GROUP BY a.product_id) d ON a.product_id=d.product_id LEFT JOIN cere_shop_group_work_detail e ON a.sku_id=e.sku_id where e.shop_group_work_id=#{shopGroupWorkId} and a.sku_id in (
#{item.skuId}
)

SELECT a.product_id,a.sku_id,b.product_name,a.weight,a.stock_number,b.if_oversold, a.SKU,c.`value`,b.if_logistics,e.seckill_price price, IF(c.image IS NULL OR c.image='',d.product_image,c.image) image FROM cere_product_sku a LEFT JOIN cere_shop_product b ON a.product_id=b.product_id LEFT JOIN (SELECT sku_id,image,GROUP_CONCAT(sku_value) `value` from cere_sku_name GROUP BY sku_id) c ON a.sku_id=c.sku_id LEFT JOIN (SELECT a.product_id,a.product_image from cere_product_image a,cere_shop_product b where a.product_id=b.product_id GROUP BY a.product_id) d ON a.product_id=d.product_id LEFT JOIN cere_shop_seckill_detail e ON a.sku_id=e.sku_id where e.shop_seckill_id=#{shopSeckillId} and a.sku_id in (
#{item.skuId}
)

SELECT a.product_id,a.sku_id,b.product_name,a.weight,a.stock_number,b.if_oversold, a.SKU,c.`value`,b.if_logistics,e.price, IF(c.image IS NULL OR c.image='',d.product_image,c.image) image FROM cere_product_sku a LEFT JOIN cere_shop_product b ON a.product_id=b.product_id LEFT JOIN (SELECT sku_id,image,GROUP_CONCAT(sku_value) `value` from cere_sku_name GROUP BY sku_id) c ON a.sku_id=c.sku_id LEFT JOIN (SELECT a.product_id,a.product_image from cere_product_image a,cere_shop_product b where a.product_id=b.product_id GROUP BY a.product_id) d ON a.product_id=d.product_id LEFT JOIN cere_shop_discount_detail e ON a.sku_id=e.sku_id where e.shop_discount_id=#{shopDiscountId} and a.sku_id in (
#{item.skuId}
)

