mapper.select:
SELECT a.product_id,a.sku_id,b.product_name,a.weight, a.SKU,c.`value`,b.if_logistics,IF(n.seckill_price IS NULL, IF(h.price IS NULL,a.price,h.price),n.seckill_price) price, IF(c.image IS NULL OR c.image='',d.product_image,c.image) image FROM cere_product_sku a LEFT JOIN cere_shop_product b ON a.product_id=b.product_id LEFT JOIN (SELECT sku_id,image,GROUP_CONCAT(sku_value) `value` from cere_sku_name GROUP BY sku_id) c ON a.sku_id=c.sku_id LEFT JOIN (SELECT a.product_id,a.product_image from cere_product_image a,cere_shop_product b where a.product_id=b.product_id GROUP BY a.product_id) d ON a.product_id=d.product_id LEFT JOIN (SELECT a.shop_seckill_id,a.sku_id,a.number,a.total,a.seckill_price FROM cere_shop_seckill_detail a INNER JOIN (SELECT a.* from (SELECT shop_seckill_id,shop_id FROM cere_shop_seckill where state=1 ORDER BY effective_start) a GROUP BY a.shop_id) b ON a.shop_seckill_id=b.shop_seckill_id) n ON a.sku_id=n.sku_id LEFT JOIN (SELECT a.shop_discount_id,a.sku_id,a.number,a.total,a.price FROM cere_shop_discount_detail a INNER JOIN (SELECT a.* from (SELECT shop_discount_id,shop_id FROM cere_shop_discount where state=1 ORDER BY start_time) a GROUP BY a.shop_id) b ON a.shop_discount_id=b.shop_discount_id) h ON a.sku_id=h.sku_id where a.sku_id in (
#{item.skuId}
)

SELECT a.product_id,a.sku_id,b.product_name,a.weight, a.SKU,c.`value`,b.if_logistics,a.price, IF(c.image IS NULL OR c.image='',d.product_image,c.image) image FROM cere_product_sku a LEFT JOIN cere_shop_product b ON a.product_id=b.product_id LEFT JOIN (SELECT sku_id,image,GROUP_CONCAT(sku_value) `value` from cere_sku_name GROUP BY sku_id) c ON a.sku_id=c.sku_id LEFT JOIN (SELECT a.product_id,a.product_image from cere_product_image a,cere_shop_product b where a.product_id=b.product_id GROUP BY a.product_id) d ON a.product_id=d.product_id where a.sku_id in (
#{item.skuId}
)

SELECT d.shop_id,a.product_id,a.sku_id,a.product_price*a.number total, a.product_price price,a.image,a.number,a.weight,a.SKU,c.`value`, a.product_name, a.logistics_price, b.pricing_price, a.activity_type, b.discount_price, a.actual_price from cere_order_product a LEFT JOIN cere_shop_order b ON a.order_id=b.order_id LEFT JOIN (SELECT GROUP_CONCAT(sku_value) `value`,sku_id FROM cere_sku_name GROUP BY sku_id) c ON a.sku_id=c.sku_id LEFT JOIN cere_platform_shop d ON b.shop_id=d.shop_id where a.order_id=#{orderId} and a.sku_id NOT in (
#{id}
)

select product_id, sum(stock_number) as stockNumber,
sum(total) as total
from cere_product_sku where product_id =
#{productId}

select sku_id
from cere_product_sku where product_id =
#{productId}

SELECT sku_value, value_code from cere_sku_name
where sku_id
= #{skuId}
and sku_name = #{skuName}
GROUP BY value_code

select a.* from cere_product_sku a join cere_sku_member_real_info b on b.sku_id = a.sku_id and b.activity_type not in
#{activityType}
and b.member_level_id = 0 join cere_shop_product c on b.product_id = c.product_id and c.shop_id = #{shopId} and c.shelve_state = 1

SELECT a.product_id, a.sku_id, a.weight, a.stock_number, a.SKU, a.original_price, a.price,
b.if_logistics, b.if_oversold, b.product_name, b.if_credit, b.credit_limit,
c.`value`, IF(c.image IS NULL OR c.image = '', d.product_image, c.image) image FROM cere_product_sku a JOIN cere_shop_product b ON a.product_id = b.product_id LEFT JOIN ( select sku_id, image, GROUP_CONCAT(sku_value) as `value` from cere_sku_name where sku_id in (
#{skuId}
) group by sku_id ) c on c.sku_id = a.sku_id LEFT JOIN cere_product_image d ON a.product_id = d.product_id where a.sku_id in (
#{skuId}
) group by a.sku_id
