mapper.select:
SELECT crc.*, IF( crc.user_type = 1, a.NAME, b.NAME ) AS userName, IF( crc.user_type = 1, a.head_image, b.avatar ) AS avatar, IF( crc.root_comment_id != crc.parent_comment_id , IF( crc.target_user_type = 1, c.NAME, d.NAME ), null ) AS targetUserName, If( crc.user_type = 1 and crc.user_id = #{userId}, 1, 0) AS isSelf FROM cere_recommend_comment crc LEFT JOIN cere_buyer_user AS a ON crc.user_id = a.buyer_user_id LEFT JOIN cere_platform_business AS b ON crc.user_id = b.business_user_id LEFT JOIN cere_buyer_user AS c ON crc.target_user_id = c.buyer_user_id LEFT JOIN cere_platform_business AS d ON crc.target_user_id = d.business_user_id where recommend_id = #{recommendId}
and crc.root_comment_id = #{recommendCommentId}
and crc.root_comment_id is null
and crc.recommend_comment_id != #{replyCommentId}
ORDER BY crc.reply_count DESC

mapper.update:
update cere_recommend_comment
set reply_count = reply_count + 1
where recommend_comment_id = #{recommendCommentId}

update cere_recommend_comment
set reply_count = reply_count - #{count}
where recommend_comment_id = #{recommendCommentId}

mapper.select:
select count(0) from cere_recommend_comment
where target_user_id = #{userId} and target_user_type = 1 and read_status = 0
and user_id != #{userId}

mapper.update:
update cere_recommend_comment
set read_status = 1
where target_user_id = #{userId}
