mapper.select:
SELECT a.business_user_id,`name`,sex,email,`username`, b.shop_id FROM cere_platform_business a
join cere_business_shop b on a.business_user_id = b.business_user_id
where a.token=#{token}

mapper.update:
update cere_platform_business set
token=#{token},update_time=#{updateTime}
where business_user_id=#{businessUserId}

mapper.select:
SELECT a.business_user_id,a.`name`,a.sex,a.phone,a.email,a.state,a.create_time,a.username FROM cere_platform_business a LEFT JOIN cere_business_shop b ON a.business_user_id=b.business_user_id where b.shop_id=#{shopId}
and (a.username like concat('%',#{search},'%') OR a.`name` like concat('%',#{search},'%'))
and a.state = #{state}
ORDER BY a.update_time DESC,a.create_time DESC
