mapper.update:
update cere_buyer_user
set growth = growth + #{growth}
where buyer_user_id = #{buyerUserId}

mapper.select:
SELECT a.buyer_user_id,IF(a.wechat_name IS NULL,a.`name`,a.wechat_name) `name`,d.ids, a.phone,b.frequency,b.total,bbu.update_time as lastTime, bbu.create_time time,d.labelName from cere_buyer_user a JOIN cere_business_buyer_user bbu on bbu.buyer_user_id = a.buyer_user_id LEFT JOIN (SELECT COUNT(*) frequency,SUM(price) total,buyer_user_id from cere_shop_order where state in (2,3,4) and shop_id = #{shopId} GROUP BY buyer_user_id) b ON a.buyer_user_id=b.buyer_user_id LEFT JOIN (SELECT a.buyer_user_id,GROUP_CONCAT(b.label_name) labelName,GROUP_CONCAT(a.label_id) ids from cere_buyer_shop_label a,cere_shop_user_label b where a.label_id=b.label_id and b.shop_id=#{shopId} GROUP BY a.buyer_user_id) d ON a.buyer_user_id=d.buyer_user_id where bbu.shop_id = #{shopId}
and bbu.update_time >= #{lastStartTime} and bbu.update_time <= #{lastEndTime}
and a.create_time>=#{startTime} and a.create_time<=#{endTime}
and a.phone like concat('%',#{phone},'%')
and (d.ids like concat('%,',#{labelId},'%') OR d.ids=#{labelId})
GROUP BY a.buyer_user_id order by bbu.update_time desc
