mapper.select:
SELECT a.order_id, a.order_formid, a.distributor_id, a.distributor_name, a.distributor_phone, a.price, a.commission, a.`state`, a.transaction_time FROM cere_distribution_order a LEFT JOIN cere_shop_order b ON a.order_id=b.order_id where b.shop_id=#{shopId} and a.type=2
and a.order_formid like concat('%',#{orderFormid},'%')
and a.distributor_name like concat('%',#{distributorName},'%')
and a.distributor_phone like concat('%',#{distributorPhone},'%')
and a.transaction_time>=#{startTime} and a.transaction_time<=#{endTime}
and a.state=#{state}

mapper.update:
UPDATE cere_distribution_order set state=1
where order_id in (

  #{id}

)

mapper.select:
SELECT distributor_id,distributor_name,distributor_phone from cere_shop_distributor
where shop_id=#{shopId}

  and distributor_name like concat('%',#{distributorName},'%')


  and distributor_phone like concat('%',#{distributorPhone},'%')


  and create_time>=#{startTime} and create_time<=#{endTime}
