  and b.shop_id=#{shopId} and c.buyer_user_id IS NOT NULL


  and b.shop_id=#{shopId} and c.buyer_user_id IS NULL


  and a.buyer_user_id in (

    #{id}

  )

GROUP BY a.buyer_user_id

