SELECT shop_crowd_id, shop_id, crowd_name, shop_buy_yes, shop_buy_no, shop_order_yes, shop_order_no,
shop_add_yes, shop_add_no, shop_visit_yes, shop_visit_no, effective_buy, effective_buy_count,
effective_price, effective_price_count, users, label_id,ids FROM cere_shop_crowd where shop_crowd_id=#{shopCrowdId}

SELECT a.shop_crowd_id, a.shop_id, crowd_name, shop_buy_yes, shop_buy_no, shop_order_yes, shop_order_no,
shop_add_yes, shop_add_no, shop_visit_yes, shop_visit_no, effective_buy, effective_buy_count,
effective_price, effective_price_count, users, b.label_id,b.label_name FROM cere_shop_crowd a
LEFT JOIN (SELECT a.label_id,b.shop_crowd_id,GROUP_CONCAT(a.label_name) label_name FROM cere_shop_user_label a,cere_shop_crowd b
WHERE b.label_id LIKE CONCAT('%,',a.label_id,'%')) b ON a.shop_crowd_id=b.shop_crowd_id
where a.shop_id=#{shopId}

  and a.crowd_name like concat('%',#{crowdName},'%')


  and a.users>=#{min}


  and a.users<=#{max}

ORDER BY a.update_time DESC,a.create_time DESC

SELECT a.buyer_user_id,IF(a.wechat_name IS NULL,a.`name`,a.wechat_name) `name`, a.phone,b.frequency,b.total,c.lastTime,a.create_time time,d.labelName from cere_buyer_user a LEFT JOIN (SELECT COUNT(*) frequency,SUM(price) total,buyer_user_id from cere_shop_order where state in (2,3,4) GROUP BY buyer_user_id) b ON a.buyer_user_id=b.buyer_user_id LEFT JOIN (SELECT buyer_user_id,create_time lastTime from cere_shop_order where state in (2,3,4) ORDER BY create_time DESC LIMIT 1) c ON a.buyer_user_id=c.buyer_user_id LEFT JOIN (SELECT a.buyer_user_id,GROUP_CONCAT(b.label_name) labelName,GROUP_CONCAT(a.label_id) labelIds from cere_buyer_shop_label a,cere_shop_user_label b where a.label_id=b.label_id GROUP BY a.buyer_user_id) d ON a.buyer_user_id=d.buyer_user_id where a.buyer_user_id in (
#{id}
) GROUP BY a.buyer_user_id

mapper.delete:
