update cere_shop_crowd




        when shop_crowd_id=#{i.shopCrowdId} then #{i.ids}






        when shop_crowd_id=#{i.shopCrowdId} then #{i.users}






        when shop_crowd_id=#{i.shopCrowdId} then #{i.updateTime}




where

  shop_crowd_id=#{i.shopCrowdId}
