mapper.select:
SELECT shop_seckill_id, shop_id, seckill_name, remark, effective_start, effective_end, if_limit,
limit_number, if_enable, enable_time, if_add, `state`,if_number,number FROM cere_shop_seckill
where shop_id=#{shopId} and state in (0,1)

    and shop_seckill_id in (

        #{id}

    )

SELECT a.shop_seckill_id,a.seckill_name,a.state,b.down_price,b.seckill_price,a.effective_start,a.effective_end FROM cere_shop_seckill a LEFT JOIN (SELECT shop_seckill_id,MAX(down_price) down_price,seckill_price from cere_shop_seckill_detail GROUP BY shop_seckill_id) b ON a.shop_seckill_id=b.shop_seckill_id where a.shop_id=#{shopId}
and a.seckill_name like concat('%',#{seckillName},'%')
and a.state=#{state}
ORDER BY a.update_time DESC,a.create_time DESC

SELECT seckill_name
from cere_shop_seckill
where shop_seckill_id = #{shopSeckillId}

SELECT IFNULL(COUNT(*), 0)
from cere_buyer_seckill_visit
where shop_seckill_id = #{shopSeckillId}

SELECT COUNT(*)
from cere_shop_order
where shop_seckill_id = #{shopSeckillId}
  and shop_id = #{shopId}
  and state in (2, 3, 4)
GROUP BY buyer_user_id

SELECT IF(SUM(price) IS NULL, 0, SUM(price))
from cere_shop_order
where shop_seckill_id = #{shopSeckillId}
  and shop_id = #{shopId}
  and state in (2, 3, 4)

SELECT *
FROM cere_shop_seckill
where shop_id = #{shopId}
  and ((if(if_enable = 2, date_sub(effective_start, interval enable_time hour), effective_start) <= #{effectiveStart} and effective_end >= #{effectiveStart})
    OR (if(if_enable = 2, date_sub(effective_start, interval enable_time hour), effective_start) <= #{effectiveEnd} and effective_end >= #{effectiveEnd}))
  and state!= 2 and shop_seckill_id!= #{shopSeckillId}

SELECT a.product_id FROM cere_shop_discount_detail a
LEFT JOIN cere_shop_discount b ON a.shop_discount_id=b.shop_discount_id
where b.shop_id = #{shopId}
and ((if(b.if_enable = 2, date_sub(b.start_time, interval b.enable_time hour), b.start_time) <= #{startTime} and b.end_time >= #{startTime})
OR (if(b.if_enable = 2, date_sub(b.start_time, interval b.enable_time hour), b.start_time) <= #{endTime} and b.end_time >= #{endTime}))
and a.product_id in (

    #{id}

)

SELECT a.product_id FROM cere_shop_group_work_detail a
LEFT JOIN cere_shop_group_work b ON a.shop_group_work_id=b.shop_group_work_id
where b.shop_id=#{shopId}
and ((if(b.if_enable = 2, date_sub(b.start_time, interval b.enable_time hour), b.start_time) <= #{startTime} and b.end_time >= #{startTime})
OR (if(b.if_enable = 2, date_sub(b.start_time, interval b.enable_time hour), b.start_time) <= #{endTime} and b.end_time >= #{endTime}))
and a.product_id in (

    #{id}

)

mapper.update:
update cere_shop_seckill SET state=4,update_time=#{time} where shop_seckill_id in (

    #{item.shopSeckillId}

)
