mapper.select:
select 
from cere_live_product
where shop_id = #{shopId}

  and state = #{state}


  and product_name like #{search}

order by update_time desc

select 
from cere_live_product
where id = #{id} and shop_id = #{shopId}

select count(id)
from cere_live_product
where product_id = #{productId}

  and id != #{id}
