mapper.select:
SELECT shop_discount_id, shop_id, discount_name, remark, start_time, end_time, if_limit,
limit_number, if_enable, enable_time, if_add, `state`,if_add,if_number,number FROM cere_shop_discount
WHERE shop_id=#{shopId} and state in (0,1)

    and shop_discount_id in (

        #{id}

    )

SELECT a.shop_discount_id,a.discount_name,CONCAT('无门槛',b.discount,'折') content,a.start_time,a.end_time,a.state FROM cere_shop_discount a LEFT JOIN (SELECT MIN(discount) discount,shop_discount_id from cere_shop_discount_detail GROUP BY shop_discount_id) b ON a.shop_discount_id=b.shop_discount_id where a.shop_id=#{shopId}
and a.discount_name like concat('%',#{discountName},'%')
and a.state=#{state}
ORDER BY a.update_time DESC,a.create_time DESC

SELECT discount_name
from cere_shop_discount
where shop_discount_id = #{shopDiscountId}

SELECT COUNT(*)
FROM cere_shop_order
where shop_id = #{shopId}
  and shop_discount_id = #{shopDiscountId}
  and state in (2, 3, 4)

SELECT IF(SUM(price) IS NULL, 0, SUM(price))
FROM cere_shop_order
where shop_id = #{shopId}
  and shop_discount_id = #{shopDiscountId}
  and state in (2, 3, 4)

SELECT *
FROM cere_shop_discount
where shop_id = #{shopId}
  and ((if(if_enable = 2, date_sub(start_time, interval enable_time hour), start_time) <= #{startTime} and end_time >= #{startTime})
    OR (if(if_enable = 2, date_sub(start_time, interval enable_time hour), start_time) <= #{endTime} and end_time >= #{endTime}))
  and state!= 2 and shop_discount_id!= #{shopDiscountId}

SELECT a.product_id FROM cere_shop_seckill_detail a
LEFT JOIN cere_shop_seckill b ON a.shop_seckill_id=b.shop_seckill_id
where b.shop_id=#{shopId}
and ((if(b.if_enable = 2, date_sub(b.effective_start, interval b.enable_time hour), b.effective_start) <= #{startTime} and b.effective_end >= #{startTime})
OR (if(b.if_enable = 2, date_sub(b.effective_start, interval b.enable_time hour), b.effective_start) <= #{endTime} and b.effective_end >= #{endTime}))
and a.product_id in (

    #{id}

)

SELECT a.product_id FROM cere_shop_group_work_detail a
LEFT JOIN cere_shop_group_work b ON a.shop_group_work_id=b.shop_group_work_id
where b.shop_id=#{shopId}
and ((if(b.if_enable = 2, date_sub(b.start_time, interval b.enable_time hour), b.start_time) <= #{startTime} and b.end_time >= #{startTime})
OR (if(b.if_enable = 2, date_sub(b.start_time, interval b.enable_time hour), b.start_time) <= #{endTime} and b.end_time >= #{endTime}))
and a.product_id in (

    #{id}

)

SELECT IFNULL(COUNT(*), 0)
from cere_buyer_discount_visit
where shop_discount_id = #{shopDiscountId}

mapper.update:
update cere_shop_discount SET state=2,update_time=#{time} where shop_discount_id in (

    #{item.shopDiscountId}

)
